import { Flex } from "@chakra-ui/react";
import {useParams} from 'react-router-dom'
import {useQuery} from '@tanstack/react-query'
import Moment from 'react-moment'
import axios from "axios";
export default function DetailDiscussionPage() {
    const {id} = useParams()

    async function fetchDiscussion() {
        const response = await axios.get(
            `http://localhost:8000/api/discussions/${id}`
        )
        const discussion = response.data
        return discussion
    }

    const {
        status,
        error,
        data: discussion,
    } = useQuery({
        queryKey: ['discussion'],
        queryFn: fetchDiscussion,
    })
    return (
        <Flex
            alignSelf={'center'}
            justify={'center'}
            marginTop={'10px'}
            paddingBottom={'20px'}>
            <Flex width={'60%'} justify={'center'} flexDirection={'column'} bg={'white'}>
            <Moment fromNow>{discussion.created_at}</Moment>
                {discussion && discussion.title}

            </Flex>
        </Flex>
    )
}
