import { Flex } from "@chakra-ui/react";
import {useParams} from 'react-router-dom'
import {useQuery} from '@tanstack/react-query'
import axios from "axios";
export default function DetailDiscussionPage() {
    const {id} = useParams()

    async function fetchDiscussion() {
        const response = await axios.get(
            `http://localhost:8000/api/discussions/${id}`
        )
        const discussion = response.data
        return discussion
    }

    const {
        status,
        error,
        data: discussion,
    } = useQuery({
        queryKey: ['discussion'],
        queryFn: fetchDiscussion,
    })
    return (
        <Flex width={'90%'} justify={'center'} align={'center'}>
            <Flex bg={'white'} width={'100%'} justifySelf={'center'}>

            {discussion&& discussion.title}
            </Flex>

        </Flex>
    )
}
