import { Flex, Text, Image } from '@chakra-ui/react'
import axios from 'axios'
import { useQuery } from '@tanstack/react-query'
import Moment from 'react-moment'
export default function Discussions() {
    async function fetchDiscussions() {
        const response = await axios.get(
            'http://localhost:8000/api/discussions'
        )
        const discussions = response.data
        return discussions
    }

    const {
        status,
        error,
        data: discussions,
    } = useQuery({
        queryKey: ['discussions'],
        queryFn: fetchDiscussions,
    })
    return (
        <Flex flexDirection='column'>
            {discussions &&
                discussions.map((discussion) => {
                    return (
                        <Flex
                            bg={'white'}
                            marginTop={'20px'}
                            borderRadius={5}
                            padding='10px'
                            flexDirection={'column'}>
                                {/**Author Avatar*/}
                                <Flex>

                                </Flex>
                                <Image width={50} height={50} borderRadius={50} bg={'gray.300'}/>
                                <Text>Posted by {discussion.author}</Text>

                            <Text>{discussion.topic}</Text>
                            <Text>{discussion.title}</Text>
                            <Moment fromNow>{discussion.created_at}</Moment>
                        </Flex>
                    )
                })}
        </Flex>
    )
}
