import { Button, Flex, Text} from "@chakra-ui/react";
import {useParams} from 'react-router-dom'
import {useQuery} from '@tanstack/react-query'
import Moment from 'react-moment'
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { editing, reading} from "../../redux/DiscussionSlice";
import {useNavigate} from 'react-router-dom'
export default function DetailDiscussionPage() {
    const discussionStatus = useSelector((state) => state.discussion.status)
    const {id} = useParams()
    const navigate = useNavigate()
    async function fetchDiscussion() {
        const response = await axios.get(
            `http://localhost:8000/api/discussions/${id}`
        )
        const discussion = response.data
        return discussion
    }

    const {
        status,
        error,
        data: discussion,
    } = useQuery({
        queryKey: ['discussion'],
        queryFn: fetchDiscussion,
    })
    if(discussionStatus !== 'reading')
    return (
        <Flex
            alignSelf={'center'}
            justify={'center'}
            marginTop={'10px'}
            paddingBottom={'20px'}>
            <Flex width={'50%'} justify={'center'} flexDirection={'column'} bg={'white'} padding={'10px'}>
            <Flex flexDirection={'column'}>
                <Flex>

                                        <Text>
                                            Posted &nbsp;
                                        </Text>
                                        <Moment fromNow>
                                            {discussion &&discussion.created_at}
                                        </Moment>
                </Flex>
                                        <Text fontWeight={'600'} fontSize={'30px'}>{discussion && discussion.title}</Text>
                                        <Text>{discussion && discussion.description}</Text>
                                        <Flex justify={'center'} align={'center'} marginTop={'20px'}>
                                        <Button width={'70px'} marginRight={'20px'}
                                        bg={'red.500'}
                                        color={'white'}
                                        _hover={{bg: 'red.400'}}
                                        onClick={() => {
                                            try{
                                                const request = axios({
                                                    method: 'DELETE',
                                                    url: `http://127.0.0.1:8000/api/discussions/${id}`,

                                                })

                                            }catch (error){
                                                console.info(error)
                                            }  
                                            navigate('/')
                                        }}
                                        >Delete</Button>
                                        <Button width={'70px'} bg={'blue.300'}
                                        color={'white'}
                                        _hover={{bg: 'blue.200'}}
                                        >Edit</Button>

                                        </Flex>
                                    </Flex>

            </Flex>
        </Flex>
    )
}
