import { Flex, Text } from "@chakra-ui/react";
import axios from "axios";
import { useQuery } from "@tanstack/react-query";
import Moment from 'react-moment'
export default function Discussions() {

    async function fetchDiscussions() {
        const response = await axios.get('http://localhost:8000/api/discussions')
        const discussions = response.data
        return discussions
    }

    const {status, error,data:discussions} = useQuery({
        queryKey: ['discussions'],
        queryFn: fetchDiscussions,
    })
    return (
        <Flex flexDirection='column'>
            {discussions && discussions.map((discussion) => {
                return (
                    <Flex bg={'white'} marginTop={'20px'} borderRadius={5} padding='10px' flexDirection={'column'}>
                        <Text>{discussion.author}</Text>
            <Text>{discussion.topic}</Text>
                    <Text>
                        {discussion.title}
                    </Text>
                    <Moment fromN>{discussion.created_at}</Moment>
                    </Flex>
                )
            })}


        </Flex>
    )
}
