import {Modal, ModalOverlay, Button, useDisclosure, ModalContent, ModalHeader, ModalFooter, ModalBody, ModalCloseButton} from "@chakra-ui/react";
import { useDispatch, useSelector } from "react-redux";
import { resetPassword } from "../../redux/AuthSlice";
export default function AuhtModal() {
    //Redux
    const  dispath = useDispatch()
    const  modalStatus = useSelector((state) => state.auth.modalStatus)


    const { isOpen, onOpen, onClose } = useDisclosure()
  return (
    <>
       <Button 
            bg={'blue.200'}
            fontWeight={'400'}
            _hover={{ bg: 'blue.100' }}
            width={'100px'}
            onClick={() => {
                onOpen()
            }}

            >Log in</Button>

      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>
            {modalStatus === 'login' && 'Log In'}
            {modalStatus === 'register' && 'Sign Up'}
            {modalStatus === 'resetPassword' && 'Reset Password'}
            </ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            Modal 
          </ModalBody>

          <ModalFooter>
            <Button colorScheme='blue' mr={3} onClick={dispath(resetPassword)}>
              Close
            </Button>
            <Button variant='ghost'>Secondary Action</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  )
}
