import { Flex, Text, Input, Textarea, Select, Button } from "@chakra-ui/react";
import axios from "axios";
import { useState, useEffect} from "react";

export default function EditDiscussionPage(id) {
    const [title, setTitle] = useState('')
    const [description, setDiscription] = useState('')
    const [discussion, setDiscussion] = useState({})
    async function fetchDiscussion() {
        const response = await axios.get(
            `http://localhost:8000/api/discussions/${id}`
        )
        setDiscussion(response.data)
    }
    useEffect(() => {

        fetchDiscussion()

    }, [])
    return (
        <Flex
        alignSelf={'center'}
        justify={'center'}
        marginTop={'10px'}
        paddingBottom={'20px'}>
        <Flex width={'50%'} justify={'center'} flexDirection={'column'} bg={'white'} padding={'10px'}>
        <Flex flexDirection={'column'}>
            <Textarea placeholder={'Title'} 

            marginBottom={'20px'} resize={'none'} maxLength={255}

            onChange={(event) => {
                setTitle(event.target.value)
            }}
            />
            <Textarea placeholder="Write Something" minHeight={'200px'} marginBottom={'20px'}
            onChange={(event) => {
                setDiscription(event.target.value)
            }}
            />
            <Button
            onClick={() => {
                if(title !== '' && description !== ''){
                    try{
                        const request = axios({
                            method: 'POST',
                            url: 'http://127.0.0.1:8000/api/discussions/',
                            data: {
                                title: title,
                                description: description,
                               // topic: topic
                            }
                        })

                    }catch (error){
                        console.info(error)
                    }  

                }
                console.log(dtitle)
                console.log(description)
            }}
            >Confirm Editing</Button>
        </Flex>

        </Flex>
    </Flex>
    )
}
