import { Flex, Text, Image } from "@chakra-ui/react";
import axios from 'axios'
import {useQuery} from '@tanstack/react-query'
import {Moment} from 'react-moment'
export default function Topics() {

    async function fetchTopics() {
        const response = await axios.get('http://localhost:8000/api/topics')
        const topics = response.data
        return topics
    }

    const {status, error,data:topics} = useQuery({
        queryKey: ['topics'],
        queryFn: fetchTopics,
    })

    return (
        <Flex flexDirection={'column'} justify='center' align={'center'} padding={'2%'} width={'100%'} borderBottom={'1px'}>
            <Text fontWeight={'600'} fontSize={'32px'}>Topics</Text>
            <Flex>
            {topics && topics.map((topic) => {
                return(
                  <Flex key={topic.id}  align={'center'} justifyContent={'center'}
                  flexDirection={'column'}
                  width={'200px'} height={'200px'}
                  backgroundImage={topic.image}
                  backgroundRepeat={'no-repeat'}
                  backgroundSize={'contain'}
                    marginRight={'2%'}
                  >
                    <Text color={'white'} fontWeight={"600"} marginBottom={'20%'} fontSize={20}>
                        {topic.name}
                    </Text>
                  </Flex>
                )
            })}
            </Flex>
        </Flex>
    )
}
