import { Flex, Text, Image } from '@chakra-ui/react'
import {Switch, Route} from 'react-router-dom'
import DetailTopic from '../detailTopicPage/DetailTopicPage'
import axios from 'axios'
import { useQuery } from '@tanstack/react-query'
export default function Topics(topic_list) {


    return (
        <Flex
            flexDirection={'column'}
            justify='center'
            align={'center'}
            padding={'2%'}
            width={'100%'}>
            <Text fontWeight={'600'} fontSize={'32px'}>
                Topics
            </Text>
            <Flex>
                {topicList &&
                    topics.map((topic) => {
                        return (
                            <Flex
                                key={topic.id}
                                align={'center'}
                                justifyContent={'center'}
                                flexWrap={'wrap'}
                                flexDirection={'column'}
                                width={'200px'}
                                height={'100px'}
                                backgroundColor={'blue.300'}
                                borderRadius={10}
                                marginRight={'2%'}
                                _hover={{ bg: 'blue.100' }}
                                cursor={'pointer'}>
                                <Text
                                    color={'white'}
                                    fontWeight={'600'}
                                    fontSize={20}>
                                    {topic.name}
                                </Text>
                            </Flex>
                        )
                    })}
            </Flex>
            <Switch>
         <Route
           path="/topics/:id"
           render={({ match }) => (
             <DetailTopic
               topic={topics.find(
                 (topic) => String(topic.id) === match.params.id
               )}
             />
           )}
         />
       </Switch>
        </Flex>

    )
}
